import { useState, useContext } from 'react';
import AuthContext from '../context/AuthContext';
import { login, register } from '../api';
import './Auth.css';

const Auth = () => {
  const { login: loginUser } = useContext(AuthContext);
  const [isRegister, setIsRegister] = useState(false);
  const [form, setForm] = useState({ email: '', password: '', username: '' });

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = isRegister ? await register(form.username, form.email, form.password) : await login(form.email, form.password);
      loginUser(response.data.token);
    } catch (error) {
      console.error('Authentication failed:', error.response?.data?.error);
    }
  };

  return (
    <div>
      <h2>{isRegister ? 'Register' : 'Login'}</h2>
      <form onSubmit={handleSubmit}>
        {isRegister && <input type="text" placeholder="Username" onChange={(e) => setForm({ ...form, username: e.target.value })} />}
        <input type="email" placeholder="Email" onChange={(e) => setForm({ ...form, email: e.target.value })} />
        <input type="password" placeholder="Password" onChange={(e) => setForm({ ...form, password: e.target.value })} />
        <button type="submit">{isRegister ? 'Register' : 'Login'}</button>
      </form>
      <button onClick={() => setIsRegister(!isRegister)}>{isRegister ? 'Already have an account? Login' : 'New user? Register'}</button>
    </div>
  );
};

export default Auth;
