import { useState, useEffect } from 'react';
import { fetchVideos } from '../api'; // API function to fetch video metadata
import { Link } from 'react-router-dom';

const VideoList = () => {
  const [videos, setVideos] = useState([]); // Store fetched videos
  const [loading, setLoading] = useState(true); // Loading state
  const [error, setError] = useState(null); // Error state
  const [page, setPage] = useState(1); // Pagination state
  const [search, setSearch] = useState(''); // Search state

  // Fetch videos when the component mounts or when the page or search query changes
  useEffect(() => {
    const loadVideos = async () => {
      try {
        const response = await fetchVideos(search, page); // Fetch video metadata with search query
        setVideos(response.data.videos); // Set the fetched videos
        setLoading(false); // Set loading to false after fetching
      } catch (err) {
        console.error('Error fetching videos:', err);
        setError('Failed to load videos.');
        setLoading(false);
      }
    };

    loadVideos();
  }, [search, page]); // Re-fetch videos when search query or page changes

  // Handle loading and error states
  if (loading) {
    return <p>Loading videos...</p>;
  }

  if (error) {
    return <p>{error}</p>;
  }

  return (
    <div>
      <h2>Uploaded Videos</h2>

      {/* Search Bar */}
      <input
        type="text"
        placeholder="Search videos..."
        value={search}
        onChange={(e) => setSearch(e.target.value)} // Update search state on input change
        style={{ marginBottom: '20px', padding: '10px', width: '50%' }}
      />

      <div className="video-grid">
        {videos.map((video) => (
          <div key={video._id} className="video-item">
            <Link to={`/videos/${video._id}`}>
              <img src={video.thumbnailUrl} alt={video.title} className="video-thumbnail" />
            </Link>
            <h3>{video.title}</h3>
          </div>
        ))}
      </div>

      {/* Pagination */}
      <div className="pagination">
        <button onClick={() => setPage(page - 1)} disabled={page === 1}>
          Previous
        </button>
        <button onClick={() => setPage(page + 1)} disabled={videos.length === 0}>
          Next
        </button>
      </div>
    </div>
  );
};

export default VideoList;
