import { useState } from 'react';
import { uploadVideo } from '../api';

const VideoUpload = () => {
  const [file, setFile] = useState(null);
  const [form, setForm] = useState({ title: '', description: '', tags: '' });

  const handleUpload = async (e) => {
    e.preventDefault();
    const formData = new FormData();
    formData.append('video', file);
    formData.append('title', form.title);
    formData.append('description', form.description);
    formData.append('tags', form.tags);

    try {
      await uploadVideo(formData);
      alert('Video uploaded successfully!');
    } catch (error) {
      console.error('Error uploading video:', error.response?.data?.error);
    }
  };

  return (
    <div>
      <h2>Upload Video</h2>
      <form onSubmit={handleUpload}>
        <input type="text" placeholder="Title" onChange={(e) => setForm({ ...form, title: e.target.value })} />
        <input type="text" placeholder="Description" onChange={(e) => setForm({ ...form, description: e.target.value })} />
        <input type="text" placeholder="Tags (comma separated)" onChange={(e) => setForm({ ...form, tags: e.target.value })} />
        <input type="file" accept="video/*" onChange={(e) => setFile(e.target.files[0])} />
        <button type="submit">Upload</button>
      </form>
    </div>
  );
};

export default VideoUpload;
