import { useState } from 'react';
import { uploadVideo } from '../api';

const VideoUpload = () => {
  const [file, setFile] = useState(null);
  const [form, setForm] = useState({ title: '', description: '', tags: '' });

  const handleUpload = async (e) => {
    e.preventDefault();
    
    if (!file) {
      alert('Please select a file.');
      return;
    }

    const formData = new FormData();
    formData.append('video', file); // Video field must match the backend field name
    formData.append('title', form.title);
    formData.append('description', form.description);
    formData.append('tags', form.tags);

    try {
      await uploadVideo(formData); // Call the API function to upload video
      alert('Video uploaded successfully!');
    } catch (error) {
      console.error('Error uploading video:', error.response?.data?.error);
    }
  };

  return (
    <div>
      <h2>Upload Video</h2>
      <form onSubmit={handleUpload}>
        <input 
          type="text" 
          placeholder="Title" 
          onChange={(e) => setForm({ ...form, title: e.target.value })} 
        />
        <input 
          type="text" 
          placeholder="Description" 
          onChange={(e) => setForm({ ...form, description: e.target.value })} 
        />
        <input 
          type="text" 
          placeholder="Tags (comma separated)" 
          onChange={(e) => setForm({ ...form, tags: e.target.value })} 
        />
        <input 
          type="file" 
          accept="video/*" 
          onChange={(e) => setFile(e.target.files[0])} // Capture the selected file
        />
        <button type="submit">Upload</button>
      </form>
    </div>
  );
};

export default VideoUpload;
