import { useState, useEffect } from 'react';
import { fetchVideos } from '../api'; // Assume this is the API call to fetch video metadata
import { Link } from 'react-router-dom';

const VideoList = () => {
  const [videos, setVideos] = useState([]);
  const [search, setSearch] = useState(''); // Add search state
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [page, setPage] = useState(1);

  useEffect(() => {
    const loadVideos = async () => {
      try {
        const response = await fetchVideos(search, page); // Pass search query and page number
        setVideos(response.data.videos);
        setLoading(false);
      } catch (err) {
        console.error('Error fetching videos:', err);
        setError('Failed to load videos.');
        setLoading(false);
      }
    };

    loadVideos();
  }, [search, page]); // Trigger when search or page changes

  if (loading) return <p>Loading videos...</p>;
  if (error) return <p>{error}</p>;

  return (
    <div>
      <h2>Uploaded Videos</h2>
      {/* Search Input */}
      <input
        type="text"
        placeholder="Search videos..."
        value={search}
        onChange={(e) => setSearch(e.target.value)} // Update search query
      />
      <div className="video-grid">
        {videos.map((video) => (
          <div key={video._id} className="video-item">
            <Link to={`/videos/${video._id}`}>
              {/* Display Thumbnail */}
              <img src={video.thumbnailUrl} alt={video.title} className="video-thumbnail" />
            </Link>
            <h3>{video.title}</h3>
          </div>
        ))}
      </div>
      {/* Pagination */}
      <div className="pagination">
        <button onClick={() => setPage(page - 1)} disabled={page === 1}>Previous</button>
        <button onClick={() => setPage(page + 1)} disabled={videos.length === 0}>Next</button>
      </div>
    </div>
  );
};

export default VideoList;
