import { useState, useEffect, useRef } from 'react';
import { useParams } from 'react-router-dom';
import videojs from 'video.js';
import { streamVideo } from '../api';

const VideoPlayer = () => {
  const { id } = useParams();
  const [videoSrc, setVideoSrc] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const playerRef = useRef(null);

  useEffect(() => {
    const fetchVideo = async () => {
      try {
        const src = await streamVideo(id);
        setVideoSrc(src);
        setLoading(false);
      } catch (err) {
        setError('Failed to load video.');
        setLoading(false);
      }
    };

    fetchVideo();

    return () => {
      if (playerRef.current) {
        playerRef.current.dispose(); // Dispose of the player when component unmounts
      }
    };
  }, [id]);

  useEffect(() => {
    if (videoSrc && playerRef.current === null) {
      playerRef.current = videojs('my-video');
    }
  }, [videoSrc]);

  if (loading) return <p>Loading video...</p>;
  if (error) return <p>{error}</p>;

  return (
    <div>
      <h2>Video Player</h2>
      <video
        id="my-video"
        className="video-js"
        controls
        preload="auto"
        width="640"
        height="264"
        data-setup="{}"
      >
        <source src={videoSrc} type="video/mp4" />
        <p className="vjs-no-js">
          To view this video please enable JavaScript, and consider upgrading
          to a web browser that supports HTML5 video.
        </p>
      </video>
    </div>
  );
};

export default VideoPlayer;
